#' Latent Transition Analysis
#'
#' 
#' @param data .
#' @param factors a list containing named lists that define the \code{label}
#'   of the factor and the \code{vars} that belong to that factor
#' @param covs .
#' @param regform .
#' @param impu .
#' @param method .
#' @param cons .
#' @param par .
#' @param reg .
#' @param par2 .
#' @param par3 .
#' @param fit .
#' @param fit1 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$post} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$fit} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$fit1} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$reg} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$member} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$text1} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text3} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text4} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$fit$asDF}
#'
#' \code{as.data.frame(results$fit)}
#'
#' @export
lta <- function(
    data,
    factors = list(
                list(label="L1[2]", vars=list())),
    covs,
    regform = "L1 ~ SEX+RACE",
    impu = "modal",
    method = "naive",
    cons = "L1,L2,L3",
    par = FALSE,
    reg = FALSE,
    par2 = FALSE,
    par3 = FALSE,
    fit = FALSE,
    fit1 = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("lta requires jmvcore to be installed (restart may be required)")

    if ( ! missing(covs)) covs <- jmvcore::resolveQuo(jmvcore::enquo(covs))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(covs), covs, NULL))


    options <- ltaOptions$new(
        factors = factors,
        covs = covs,
        regform = regform,
        impu = impu,
        method = method,
        cons = cons,
        par = par,
        reg = reg,
        par2 = par2,
        par3 = par3,
        fit = fit,
        fit1 = fit1)

    analysis <- ltaClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

