#' Profile plot
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param group .
#' @param mc .
#' @param angle a number from 0 to 90 defining the angle of the x-axis labels,
#'   where 0 degrees represents completely horizontal labels.
#' @param plot1 .
#' @param plot2 .
#' @param width .
#' @param height .
#' @param width1 .
#' @param height1 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$mc} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$mc$asDF}
#'
#' \code{as.data.frame(results$mc)}
#'
#' @export
profile <- function(
    data,
    vars,
    group,
    mc = TRUE,
    angle = 0,
    plot1 = FALSE,
    plot2 = FALSE,
    width = 500,
    height = 500,
    width1 = 500,
    height1 = 500) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("profile requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(group), group, NULL))

    for (v in group) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- profileOptions$new(
        vars = vars,
        group = group,
        mc = mc,
        angle = angle,
        plot1 = plot1,
        plot2 = plot2,
        width = width,
        height = height,
        width1 = width1,
        height1 = height1)

    analysis <- profileClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

